</details>
</$list>"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/counts":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/counts","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/analyse" "#2d2e36">> 占比","tags":"$:/tags/TP/under-day","text":"<$let
	time_day_all={{{ [tag<currentTiddler>has[time_spent]get[time_spent]sum[]]  }}}
    time_day_life={{{ [tag<currentTiddler>has[time_spent]time_major[生活]get[time_spent]sum[]]  }}}
    time_day_study={{{ [tag<currentTiddler>has[time_spent]time_major[学习]get[time_spent]sum[]]  }}}
    time_day_working={{{ [tag<currentTiddler>has[time_spent]time_major[工作]get[time_spent]sum[]]  }}}
    time_day_amusement={{{ [tag<currentTiddler>has[time_spent]time_major[娱乐]get[time_spent]sum[]]  }}}
    time_day_activites={{{ [tag<currentTiddler>has[time_spent]time_major[活动]get[time_spent]sum[]]  }}}
    day_life_count={{{ [tag<currentTiddler>time_major[生活]count[]]  }}}
    day_study_count={{{ [tag<currentTiddler>time_major[学习]count[]]  }}}
    day_working_count={{{ [tag<currentTiddler>time_major[工作]count[]]  }}}
    day_amusement_count={{{ [tag<currentTiddler>time_major[娱乐]count[]]  }}}
    day_activites_count={{{ [tag<currentTiddler>time_major[活动]count[]]  }}}
    day_all_count={{{ [tag<currentTiddler>has[time_major]count[]]  }}}
>

|!类型|!生活|!学习|!工作|!娱乐|!活动|!总共|
|!时间|<<time_day_life>>|<<time_day_study>>|<<time_day_working>>|<<time_day_amusement>>|<<time_day_activites>>|<<time_day_all>>|
|!数量|<<day_life_count>>|<<day_study_count>>|<<day_working_count>>|<<day_amusement_count>>|<<day_activites_count>>|<<day_all_count>>|


<$macrocall $name=donut-chart pct={{{ [<time_day_life>divide<time_day_all>fixed[4]multiply[100]round[]] }}}  label="生活占比" color=green/>
<$macrocall $name=donut-chart pct={{{ [<time_day_study>divide<time_day_all>fixed[4]multiply[100]round[]] }}}  label="学习占比" color=blue/>
<$macrocall $name=donut-chart pct={{{ [<time_day_working>divide<time_day_all>fixed[4]multiply[100]round[]] }}}  label="工作占比" color=orange/>
<$macrocall $name=donut-chart pct={{{ [<time_day_amusement>divide<time_day_all>fixed[4]multiply[100]round[]] }}}  label="娱乐占比" color=red/>
<$macrocall $name=donut-chart pct={{{ [<time_day_activites>divide<time_day_all>fixed[4]multiply[100]round[]] }}}  label="活动占比" color=pink/>
<$macrocall $name=donut-chart pct={{{ [<time_day_all>divide[1440]fixed[4]multiply[100]round[]] }}}  label="一天占比" color=brown/>

<$button tooltip="一天结束后，进行赋值">
<$action-setfield
time_day_all=<<time_day_all>>
time_day_life=<<time_day_life>>
time_day_study=<<time_day_study>>
time_day_working=<<time_day_working>>
time_day_amusement=<<time_day_amusement>>
time_day_activites=<<time_day_activites>>
/>
<<doc-svg "$:/plugins/mabuqian/time-prints/image/day" "#2d2e36">> 赋值 {{!!time_day_all}}
</$button>

</$let>"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/day-dynamic-table":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/day-dynamic-table","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/day" "#2d2e36">> 一天","tags":"$:/tags/TP/under-day","text":"<$macrocall $name=aggregation  filter="[time_day<currentTiddler>]" defaultFields={{$:/plugins/mabuqian/time-prints/Config/day-dynamic-table}} class="w-100" state="TP-day-dynamic-table-state" />"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/datails":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/datails","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/list-details" "#2d2e36">> 列出","tags":"$:/tags/TP/under-day","text":"<$let graphRootTiddler=<<currentTiddler>> >

<$list filter='[tag<currentTiddler>sort[time_major]each[time_major]]'>

!! {{!!time_major}}

<div style="column-count: 4;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-between;
    align-content: stretch;">

<$list filter='[tag<graphRootTiddler>time_major{!!time_major}]'>

<$link><<currentTiddler>></$link>&nbsp;&nbsp;{{!!time_spent}}

</$list>

</div>

</$list>
</$let>"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/pie-chart":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/pie-chart","tags":"$:/tags/TP/under-day","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/analyse" "#2d2e36">> 饼图","text":"\define pie-chart()
option = {
  title: {
    text: '一天各项占比',
    left: 'center'
  },
  tooltip: {
    trigger: 'item',
    formatter: '{b} : {c} ({d}%)'
  },
  legend: {
    bottom: 10,
    left: 'center',
    data: ['生活', '学习', '工作', '娱乐', '活动']
  },
  series: [
    {
      type: 'pie',
      radius: '65%',
      center: ['50%', '50%'],
      selectedMode: 'single',
      data: [
        {value: $(time_day_life)$,name: '生活',itemStyle: {color: '#28a745'}},
        {value: $(time_day_study)$,name: '学习',itemStyle: {color: '#0e90d2'}},
        {value: $(time_day_working)$,name: '工作',itemStyle: {color: 'orange'}},
        {value: $(time_day_amusement)$,name: '娱乐',itemStyle: {color: 'red'}},
        {value: $(time_day_activites)$,name: '活动',itemStyle: {color: 'pink'}}

      ],
      emphasis: {
        itemStyle: {
          shadowBlur: 10,
          shadowOffsetX: 0,
          shadowColor: 'rgba(0, 0, 0, 0.5)'
        }
      }
    }
  ]
};
\end

<$let
	  time_day_all={{{ [tag<currentTiddler>has[time_spent]get[time_spent]sum[]]  }}}
    time_day_life={{{ [tag<currentTiddler>has[time_spent]time_major[生活]get[time_spent]sum[]]  }}}
    time_day_study={{{ [tag<currentTiddler>has[time_spent]time_major[学习]get[time_spent]sum[]]  }}}
    time_day_working={{{ [tag<currentTiddler>has[time_spent]time_major[工作]get[time_spent]sum[]]  }}}
    time_day_amusement={{{ [tag<currentTiddler>has[time_spent]time_major[娱乐]get[time_spent]sum[]]  }}}
    time_day_activites={{{ [tag<currentTiddler>has[time_spent]time_major[活动]get[time_spent]sum[]]  }}}>

<$echarts $text=<<pie-chart>> />

</$let>"},"$:/tags/TP/under-day":{"title":"$:/tags/TP/under-day","list":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/time-spare $:/plugins/mabuqian/time-prints/ViewTemplate/under-day/timeline $:/plugins/mabuqian/time-prints/ViewTemplate/under-day/counts $:/plugins/mabuqian/time-prints/ViewTemplate/under-day/datails $:/plugins/mabuqian/time-prints/ViewTemplate/under-day/pie-chart $:/plugins/mabuqian/time-prints/ViewTemplate/under-day/day-dynamic-table","text":"use this tags to display today events"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/time-spare":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/time-spare","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/time-spare" "#2d2e36">> 剩余","tags":"$:/tags/TP/under-day","text":"<$let
 time-spare-now={{{ [tag<currentTiddler>has[time_spent]sort[time_end]last[1]get[time_end]]  }}}
 time-spare-hour={{{ [<time-spare-now>first[2]multiply[60]]  }}}
 time-spare-minute={{{ [<time-spare-now>split[]last[2]join[]] }}}
 time_day_all={{{ [<time-spare-hour>add<time-spare-minute>]  }}}
 now_hour=<<now "0hh" >>
 now_mm=<<now "0mm" >>
 now_time={{{ [<now_hour>multiply[60]add<now_mm>]  }}}
 time-spare={{{ [[1440]subtract<time_day_all>] }}}
 time-real-spare={{{ [[1440]subtract<now_time>]  }}} >
一天中实际剩余：<<time-real-spare>> 分钟

还可记录：<<time-spare>> 分钟
</$let>

最后两次结束时刻

<$list filter="[tag<currentTiddler>has[time_end]sort[time_end]last[2]]">
<$link><<currentTiddler>></$link>&nbsp;&nbsp;&nbsp;&nbsp;{{!!time_end}}&nbsp;&nbsp;&nbsp;&nbsp;
</$list>"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/timeline":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-day/timeline","caption":"<<doc-svg "$:/plugins/mabuqian/time-prints/image/timeline" "#2d2e36">> 时记","tags":"$:/tags/TP/under-day","text":"<div style="column-count: 3;">
<$list filter="[tag<currentTiddler>sort[time_start]]">

<$transclude $tiddler=<<currentTiddler>> $field="time_start"/>-><$transclude $tiddler=<<currentTiddler>> $field="time_end"/>&nbsp;&nbsp;<$link><<currentTiddler>></$link>&nbsp;&nbsp;<$transclude $tiddler=<<currentTiddler>> $field="time_spent"/>

</$list>
</div>"},"$:/plugins/mabuqian/time-prints/view-template/under-event/components/buttons":{"title":"$:/plugins/mabuqian/time-prints/view-template/under-event/components/buttons","text":"\define set-time-end()
<$button tooltip="确定当前时刻结束时刻">
<$action-setfield time_end=<<now "0hh:0mm" >>/>
<<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> 当前-结束
</$button>
\end

\define set-time-start()
<$button tooltip="确定当前时刻开始时刻">
<$action-setfield time_start=<<now "0hh:0mm" >>/>
<<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> 当前-开始
</$button>
\end

\define set-time-end-start()
<$button tooltip="确定结束时刻为开始时刻">
<$action-setfield time_end={{!!time_start}}/>
<<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> 结束-开始
</$button>
\end

\define set-time-start-end()
<$let
	end-hh-ss={{{ [{!!time_end}search-replace[:],[]]  }}}
	end-hh={{{ [{!!time_end}split[:]] }}}
	end-ss={{{ [<end-hh-ss>search-replace<end-hh>,[]]  }}}
	time-end={{{ [<end-hh>multiply[60]add<end-ss>] }}}
    start-hh-ss={{{ [{!!time_start}search-replace[:],[]]  }}}
	start-hh={{{ [{!!time_start}split[:]] }}}
	start-ss={{{ [<start-hh-ss>search-replace<start-hh>,[]]  }}}
	time-start={{{ [<start-hh>multiply[60]add<start-ss>] }}}
    time_spent={{{ [<time-end>subtract<time-start>] }}}>
<$button tooltip="点击确定花费时间">
<$action-setfield time_spent=<<time_spent>> />
花费时间 <<time_spent>>
</$button>
</$let>
\end

\define create-events-under-event()
<$button tooltip="点击创建事件，在最后一次时间基础上">
    <$action-createtiddler
      $basetitle={{$:/state/TP/PageLayout/create-tiddler-title}}
      super-tag="time"
      time_form="events"
      time_major="生活"
	  time_specific=""
      time_record="正常记录"
      time_day={{!!tags}}
      tags={{!!tags}}
      time_start={{!!time_end}}
      time_end=<<now "0hh:0mm" >>
     >
     <<after-create-tiddler-without-parent>>
     </$action-createtiddler>
     <<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> 事件
</$button>
\end

\define create-events-specific-under-event()
<$button tooltip="快速创建事件">
    <$action-createtiddler
      $basetitle={{{ [[$:/state/select-filter-search/tp-time_specific]get[text]] }}}
      super-tag="time"
      time_form="events" 
      time_major={{{ [[$:/state/select-field-search/tp-time_major]get[text]] [[生活]] +[first[]] }}}
	    time_specific={{{ [[$:/state/select-filter-search/tp-time_specific]get[text]] }}}
      time_record="正常记录"
      time_day={{!!tags}}
      tags={{!!tags}}
      time_start={{!!time_end}}
      time_end=<<now "0hh:0mm" >>
     >
     <<after-create-tiddler-without-parent>>
     </$action-createtiddler>
     <<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> specific
</$button>
\end

\define create-events-detail-under-event()
<$button tooltip="快速创建事件">
    <$action-createtiddler
      $basetitle={{{ [[$:/state/select-filter-search/tp-time_detail]get[text]] }}}
      super-tag="time"
      time_form="events"
      time_major={{{ [[$:/state/select-field-search/tp-time_major]get[text]] [[生活]] +[first[]] }}}
	    time_specific={{{ [[$:/state/select-filter-search/tp-time_specific]get[text]] }}}
      time_detail={{{ [[$:/state/select-filter-search/tp-time_detail]get[text]] }}}
      time_record="正常记录"
      time_day={{!!tags}}
      tags={{!!tags}}
      time_start={{!!time_end}}
      time_end=<<now "0hh:0mm" >>
     >
     <<after-create-tiddler-without-parent>>
     </$action-createtiddler>
     <<doc-svg "$:/plugins/mabuqian/time-prints/image/event" "#2d2e36">> detail
</$button>
\end"},"$:/plugins/mabuqian/time-prints/ViewTemplate/under-event/components":{"title":"$:/plugins/mabuqian/time-prints/ViewTemplate/under-event/components","tags":"$:/tags/ViewTemplate","list-before":"$:/core/ui/ViewTemplate/body","text":"\import  [[$:/plugins/mabuqian/time-prints/view-template/under-event/components/buttons]] [[$:/plugins/mabuqian/time-prints/main/components/buttons]] [[$:/plugins/mabuqian/time-prints/view-template/under-event/components/select-edit-specific]]

<$list filter="[<currentTiddler>time_form[events]!prefix[$:/]]">
<div class="tp-sidebar-project-list-item" >
<$list filter="[<currentTiddler>tags[]tagging[]] +[has[time_end]sort[time_end]last[1]]">
最后一件事：&nbsp;<$link><<currentTiddler>></$link>&nbsp;&nbsp;{{!!time_end}}&nbsp;&nbsp;
</$list>
<<set-time-start>>&nbsp;&nbsp;
