  "type": "object",
  "properties": {
    "time_major": {
      "type": "string",
      "title": "大分类",
      "description": "时间花费的大分类，方便后续统计",
      "enum": [
        "学习",
        "生活",
        "娱乐",
        "活动",
        "工作"
      ],
      "default": "生活"
    },
    "time_spent": {
      "type": "string",
      "description": "时间花费不能为空",
      "title": "时间花费"
    },
    "time_record": {
      "type": "string",
      "title": "记录情况",
      "description": "时间记录情况",
      "enum": [
        "意外状态",
        "事后补记",
        "琐碎时间",
        "跨天记录",
        "混沌时间",
        "正常记录"
      ],
      "default": "正常记录"
    },
    "time_form": {
      "type": "string",
      "title": "周期分类",
