    "created": "20220914225904422",
    "text": "(function () {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    exports.name = "tw-game-2048-event-handler";
    exports.platforms = ["browser"];
    exports.after = ["startup"];

    exports.startup = function () {
        var g2048 = require("$:/plugins/miao/tw-2048/2048-Core.js")
        $tw.rootWidget.addEventListener("tm-game-2048-start", async function (event) {

            g2048.game.start();

            $tw.rootWidget.addEventListener("tm-game-2048-A", async function (event) {
                g2048.game.moveLeft();
            });
            $tw.rootWidget.addEventListener("tm-game-2048-D", async function (event) {
                g2048.game.moveRight();
            });
            $tw.rootWidget.addEventListener("tm-game-2048-W", async function (event) {
                g2048.game.moveTop();
            });
            $tw.rootWidget.addEventListener("tm-game-2048-S", async function (event) {
                g2048.game.moveButtom();
            });
            //使用Ctrl+方向键的快捷键。增加游戏体验。
            document.onkeydown = function (e) {
                if (e.keyCode == 37 && e.ctrlKey) {
                    g2048.game.moveLeft();
                }
                if (e.keyCode == 39 && e.ctrlKey) {
                    g2048.game.moveRight();
                }
                if (e.keyCode == 38 && e.ctrlKey) {
                    g2048.game.moveTop();
                }
                if (e.keyCode == 40 && e.ctrlKey) {
                    g2048.game.moveButtom();
                }
            };
        });
    };

})();",
