module.exports = function loginPage() {
  const loginForm = document.getElementById('om-login-form');
  const password = document.getElementById('om-password');

  loginForm.addEventListener('submit', (e) => {
    e.preventDefault();
    console.log(password.value);
  });
};
","type":"application/javascript","module-type":"library"},"loginPage":{"title":"loginPage","tags":"$:/tags/PageTemplate","text":"<div class="bg-[#f5f5ee] inset-0 fixed z-[900] flex items-center justify-center h-screen id:om-login">
  <div class="bg-white p-8 rounded shadow w-96">
    <form id="om-login-form">
      <div class="mb-6 flex flex-col">
        <label for="password"  class="block text-gray-700 text-sm font-bold mb-2"
          >密码</label>
        <input
