  <$list filter="[<currentTiddler>addprefix[$:/temp/oeyoews/BacklinkBox/]]" variable="stateTiddler">
    <!-- <$button popup=<<stateTiddler>> class="opacity-70 select-none cursor-pointer inline-flex items-center flex-shrink-0 whitespace-nowrap h-7 rounded-md pl-1 pr-2 bg-transparent border-none transition-all duration-300" tooltip="Show backlinks"> -->
    <!-- TODO: have capitalize button bug -->
    <$button popup=<<stateTiddler>> class="inline-flex items-center px-2 py-1 border border-transparent rounded-md font-semibold text-xs text-white bg-indigo-500 hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" tooltip="Show backlinks">
      {{$:/core/images/link}}&nbsp;<strong><<backlinkCount>> backlink<$reveal type="nomatch" text=<<backlinkCount>> default="1">s</$reveal></strong>
    </$button>
    <$reveal type="popup" state=<<stateTiddler>> position="below" tag="div" class="border rounded-md bg-white  max-h-72 w-min-90vw md:w-72 shadow-md overflow-hidden filter-contrast-90 pb-1 mt-2">
      <div class="text-sm leading-4 text-ellipsis overflow-hidden my-3 mx-2 flex-1 pl-2">Linked to <$reveal type="nomatch" text=<<backlinkCount>> default="1">these pages</$reveal><$reveal type="match" text=<<backlinkCount>> default="1">this page</$reveal>:</div>
      <$list filter="[<currentTiddler>!is[draft]backlinks[]!is[draft]!is[shadow]!is[system]]">
        <$link class="cursor-pointer w-full text-black capitalize text-indigo-500 hover:underline filter-contrast-70 flex items-center mb-2 mr-4 py-1 px-3 border-b border-gray-300 transition-all duration-300" to=<<currentTiddler>>>
          <div class="mr-4 font-symbol">
            <$list filter="[<currentTiddler>get[icon]]" variable="icon">
              <$list filter="[title<icon>is[shadow]]"><$transclude tiddler=<<icon>>/></$list>
              <$list filter="[title<icon>is[tiddler]]"><$transclude tiddler=<<icon>>/></$list>
              <$list filter="[title<icon>!is[shadow]!is[tiddler]]"><$wikify name="rendered" text=<<icon>> output=html><<rendered>></$wikify></$list>
            </$list>
          </div>
          ''<$text text=<<currentTiddler>>/>''
        </$link>
      </$list>
    </$reveal>
  </$list>
</$list>"},"addon/clock.js":{"title":"addon/clock.js","text":"/*\
title: addon/clock.js
module-type: echarts-component
type: application/javascript
description: clock with echarts
\*/

const Clock = {
  onUpdate(myChart) {
    option = {
      title: {
        text: '',
        top: 'top',
        left: 'center',
      },
      series: [
        {
          name: 'hour',
          type: 'gauge',
          startAngle: 90,
          endAngle: -270,
          min: 0,
          max: 12,
          splitNumber: 12,
          clockwise: true,
          axisLine: {
            lineStyle: {
              width: 8,
              color: [[1, 'rgba(0,0,0,0.7)']],
              shadowColor: 'rgba(0, 0, 0, 0.5)',
              shadowBlur: 15,
            },
          },
          splitLine: {
            lineStyle: {
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 3,
              shadowOffsetX: 1,
              shadowOffsetY: 2,
            },
          },
          axisLabel: {
            fontSize: 16,
            distance: 15,
            formatter: function (value) {
              if (value === 0) {
                return '';
              }
              return value + '';
            },
          },
          pointer: {
            icon: 'path://M2.9,0.7L2.9,0.7c1.4,0,2.6,1.2,2.6,2.6v115c0,1.4-1.2,2.6-2.6,2.6l0,0c-1.4,0-2.6-1.2-2.6-2.6V3.3C0.3,1.9,1.4,0.7,2.9,0.7z',
            width: 6,
            length: '55%',
            offsetCenter: [0, '8%'],
            itemStyle: {
              color: '#C0911F',
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 8,
              shadowOffsetX: 2,
              shadowOffsetY: 4,
            },
          },
          detail: {
            show: false,
          },
          title: {
            offsetCenter: [0, '30%'],
          },
          data: [
            {
              value: 0,
            },
          ],
        },
        {
          name: 'minute',
          type: 'gauge',
          startAngle: 90,
          endAngle: -270,
          min: 0,
          max: 60,
          clockwise: true,
          axisLine: {
            show: false,
          },
          splitLine: {
            show: false,
          },
          axisTick: {
            show: false,
          },
          axisLabel: {
            show: false,
          },
          pointer: {
            icon: 'path://M2.9,0.7L2.9,0.7c1.4,0,2.6,1.2,2.6,2.6v115c0,1.4-1.2,2.6-2.6,2.6l0,0c-1.4,0-2.6-1.2-2.6-2.6V3.3C0.3,1.9,1.4,0.7,2.9,0.7z',
            width: 4,
            length: '70%',
            offsetCenter: [0, '8%'],
            itemStyle: {
              color: '#C0911F',
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 8,
              shadowOffsetX: 2,
              shadowOffsetY: 4,
            },
          },
          anchor: {
            show: true,
            size: 20,
            showAbove: false,
            itemStyle: {
              borderWidth: 8,
              borderColor: '#C0911F',
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 8,
              shadowOffsetX: 2,
              shadowOffsetY: 4,
            },
          },
          detail: {
            show: false,
          },
          title: {
            offsetCenter: ['0%', '-40%'],
          },
          data: [
            {
              value: 0,
            },
          ],
        },
        {
          name: 'second',
          type: 'gauge',
          startAngle: 90,
          endAngle: -270,
          min: 0,
          max: 60,
          animationEasingUpdate: 'bounceOut',
          clockwise: true,
          axisLine: {
            show: false,
          },
          splitLine: {
            show: false,
          },
          axisTick: {
            show: false,
          },
          axisLabel: {
            show: false,
          },
          pointer: {
            icon: 'path://M2.9,0.7L2.9,0.7c1.4,0,2.6,1.2,2.6,2.6v115c0,1.4-1.2,2.6-2.6,2.6l0,0c-1.4,0-2.6-1.2-2.6-2.6V3.3C0.3,1.9,1.4,0.7,2.9,0.7z',
            width: 2,
            length: '85%',
            offsetCenter: [0, '8%'],
            itemStyle: {
              color: '#C0911F',
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 8,
              shadowOffsetX: 2,
              shadowOffsetY: 4,
            },
          },
          anchor: {
            show: true,
            size: 15,
            showAbove: true,
            itemStyle: {
              color: '#C0911F',
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              shadowBlur: 8,
              shadowOffsetX: 2,
              shadowOffsetY: 4,
            },
          },
          detail: {
            show: false,
          },
          title: {
            offsetCenter: ['0%', '-40%'],
          },
          data: [
            {
              value: 0,
            },
          ],
        },
      ],
    };
    setInterval(function () {
      var date = new Date();
      var second = date.getSeconds();
      var minute = date.getMinutes() + second / 60;
      var hour = (date.getHours() % 12) + minute / 60;
      option.animationDurationUpdate = 300;
      myChart.setOption({
        series: [
          {
            name: 'hour',
            animation: hour !== 0,
            data: [{ value: hour }],
          },
          {
            name: 'minute',
            animation: minute !== 0,
            data: [{ value: minute }],
          },
          {
            animation: second !== 0,
            name: 'second',
            data: [{ value: second }],
          },
        ],
      });
    }, 1000);

    myChart.setOption(option);
  },

  shouldUpdate() {
    return false;
  },
};

module.exports = Clock;
","module-type":"echarts-component","type":"application/javascript","description":"clock with echarts"},"addon/sevenday.js":{"title":"addon/sevenday.js","text":"/*\
title: addon/sevenday.js
module-type: echarts-component
type: application/javascript
description: seven
\*/

// TODO: 简化时间的处理
// TODO: 指定线的颜色, 区域的颜色
// @description: echarts 几乎支持每一处的样式设置, 这里仅根据需要设置必要的样式
const getData = (date, type = 'created') =>
  $tw.wiki.filterTiddlers(`[sameday:${type}[${date}]!is[system]!has[draft.of]]`)
    .length;

function parsesixDate(dateString) {
  const year = parseInt(dateString.substr(0, 4));
  const month = parseInt(dateString.substr(4, 2)) - 1; // 月份从0开始，需要减1
  const day = parseInt(dateString.substr(6, 2));
  const realDate = new Date(year, month, day);
  //.toLocaleDateString();
  return realDate;
}

function getSevenDaysBefore(dateString, daysLength = 7) {
  const currentDate = dateString ? parsesixDate(dateString) : new Date();

  const sevenDays = [];

  for (let i = 0; i < daysLength; i++) {
    // 获取当前日期的年、月、日
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth() + 1; // 月份从0开始，需要加1
    const day = currentDate.getDate();

    // 将年、月、日格式化成字符串，并添加到数组
    const dateString = `${year}${month < 10 ? '0' : ''}${month}${
      day < 10 ? '0' : ''
    }${day}`;
    sevenDays.unshift(dateString); // 使用unshift方法将日期添加到数组头部

    // 将当前日期减一天，以便生成前一天的日期
    currentDate.setDate(currentDate.getDate() - 1);
  }

  return sevenDays;
}

const Sevendays = {
  onUpdate(myChart, _state, addonAttributes) {
    /**
     * @param days 指定天数
     * @param date 指定日期
     * @param title 标题
     * @param subtitle 副标题
     */
    const {
      days,
      date,
      title: text = '最近文章动态',
      subtitle: subtext = '',
      disableClick = 'no',
      // smooth = 'true',
    } = addonAttributes;

    const sevendays = getSevenDaysBefore(date, days);

    // TODO: 封装成函数 https://echarts.apache.org/examples/en/editor.html?c=bump-chart
    const createdData = [];
    const modifiedData = [];

    sevendays.forEach((date) => createdData.push(getData(date)));
    sevendays.forEach((date) => modifiedData.push(getData(date, 'modified')));

    const option = {
      title: {
        text,
        subtext,
        left: 'center',
        top: 'bottom',
      },
      legend: {
        data: ['created', 'modified'],
      },
      toolbox: {
        feature: {
          restore: {},
        },
      },
      tooltip: {
        trigger: 'item', // item
        formatter: function (params) {
          const { name: date, value: count, seriesName } = params;
          const realDate = parsesixDate(date).toLocaleDateString();
          if (seriesName === 'created') {
            return count
              ? `${realDate} 新增了 ${count} 篇文章`
              : `${realDate} 没有新增文章`;
          } else {
            return count
              ? `${realDate} 更新了 ${count} 篇文章`
              : `${realDate} 没有文章更新`;
          }
        },
      },
      // color: [''],
      xAxis: {
        boundaryGap: false,
        type: 'category',
        data: sevendays,
        name: '日期',
      },
      yAxis: {
        type: 'value',
        name: '文章数量',
      },
      animationDuration: 2000,
      series: [
        {
          name: 'created',
          data: createdData,
          showSymbol: true,
          type: 'line',
          symbolSize: 10,
          endLabel: {
            show: true,
            formatter: '{a}',
            distance: 20,
          },
          // areaStyle: {
          // normal: {
          //   color: "green", //改变区域颜色
          //   lineStyle: {
          //     color: "green", //改变折线颜色
          //   },
          // },
          // },
          lineStyle: {
            width: 4,
            // color: 'purple'
          },
          emphasis: {
            focus: 'series',
            itemStyle: {
              scale: 1.5,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)',
            },
          },
          smooth: true,
        },
        {
          name: 'modified',
          data: modifiedData,
          lineStyle: {
            width: 4,
          },
          symbolSize: 10,
          type: 'line',
          showSymbol: true,
          endLabel: {
            show: true,
            formatter: '{a}',
            distance: 20,
          },
          // areaStyle: {},
          emphasis: {
            focus: 'series',
            itemStyle: {
              // color: '',
              scale: 1.25,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)',
            },
          },
          smooth: true,
        },
      ],
    };

    myChart.setOption(option);
    myChart.on('dblclick', 'series', function (params) {
      const { name: date, value: count, seriesName } = params;
      const goto = new $tw.Story();
      const filter = `[sameday:${seriesName}[${date}]!is[system]!has[draft.of]]`;

      if (!count) return;
      $tw.rootWidget.invokeActionString(
        '<$action-setfield $tiddler="$:/temp/advancedsearch" text="""' +
          filter +
          '"""/><$action-setfield $tiddler="$:/temp/advancedsearch/input" text="""' +
          filter +
          '"""/><$action-setfield $tiddler="$:/temp/advancedsearch/refresh" text="yes"/><$action-setfield $tiddler="$:/state/tab--1498284803" text="$:/core/ui/AdvancedSearch/Filter"/>',
      );
      goto.navigateTiddler('$:/AdvancedSearch');
    });
  },
  shouldUpdate() {
    return false;
  },
};

module.exports = Sevendays;
","module-type":"echarts-component","type":"application/javascript","description":"seven"},"addon/tagpie.js":{"title":"addon/tagpie.js","text":"/*\
title: addon/tagpie.js
module-type: echarts-component
type: application/javascript
description: tag pie on tiddlywiki

\*/

const getData = (tag) => {
  return {
    name: tag,
    value: $tw.wiki.filterTiddlers(`[tag[${tag}]!has[draft.of]]`).length,
  };
};

const goto = new $tw.Story();

const gotoTagTiddler = (params) => {
  const title = params.name;
  const existTiddler = $tw.wiki.tiddlerExists(title);
  if (!existTiddler) {
    console.log(title + ' not found');
    return;
  }
  // parentWidget.dispatchEvent( { type: 'tm-navigate', navigateTo: title } )
  goto.navigateTiddler(title);
};

const TagPie = {
  onUpdate(myChart, _, addonAttributes) {
    /*
     * @description 理论上所有的option配置都可以暴露出来, 这里仅仅暴露一些常用的配置, echarts将这些工作交给了addon, 大概是不同类型的addon处理起来比较复杂,但是与此同时,addon就更具有扩展性
     * @param filter 默认是用户的所有tiddler, 但是你也可以使用 filter='[tag[Journal]]' 列出所有的 Journal tiddler
     * @param title
     * @param radius
     * @param sort {descend|ascend}
     * @param doughnut {'yes'}
     * @param width {0}
     * @param legend {'yes'}
     * @param toolbox {'show'|'hide'}
     */
    const {
      title: text,
      filter = '[tags[]!prefix[$:/]]',
      sort = 'descend',
      width = 2,
      radius = 10,
      toolbox = 'hide',
      doughnut,
      focusSelf,
      legend,
    } = addonAttributes;
    // NOTE: data必须在执行onUpdate函数的时候获取到最新数据,不要写在onUpdate函数外面
    const data = [];

    // alpha sort default
    // 超过50不显示
    const tags = $tw.wiki.filterTiddlers(filter).slice(0, 50).sort();
    tags.forEach((tag) => data.push(getData(tag)));

    const borderWidth = data.length > 10 ? 0 : width;
    const borderRadius = data.length > 10 ? 5 : radius;

    // 如果类型过多, width 自动设置为0, 此时无视用户的width配置
    // 配置具体参考echarts官方文档
    const option = {
      title: {
        text,
        subtext: '',
        left: 'left',
        top: 'top',
      },
      toolbox: {
        show: toolbox === 'show' ? true : false,
        left: 0,
        bottom: 0,
        feature: {
          dataView: { show: true, readOnly: false },
          restore: {},
          saveAsImage: {},
        },
      },
      tooltip: {
        trigger: 'item',
        formatter: function (params) {
          const { name, value, percent } = params;
          if (value) {
            return `${name} 标签 有 ${value} 个条目 (${percent}%)`;
          } else {
            return `${name} 条目`;
          }
        },
      },
      legend: {
        show: legend === 'yes' ? true : false,
        orient: 'vertical',
        right: 10,
        top: 20,
        bottom: 20,
        type: 'scroll',
      },
      series: [
        {
          name: 'Tag',
          type: 'pie',
          radius: doughnut === 'yes' ? ['40%', '70%'] : '50%',
          center: legend === 'yes' ? ['40%', '50%'] : '50%',
          data,
          itemStyle: {
            borderRadius,
            borderWidth,
            borderColor: '#fff',
          },
          emphasis: {
            focus: focusSelf === 'yes' ? 'self' : '',
            itemStyle: {},
          },
        },
      ],
    };

    // descend or ascend sort
    data.sort(function (a, b) {
      return sort === 'descend' ? b.value - a.value : a.value - b.value;
    });

    myChart.setOption(option);
    myChart.on('dblclick', 'series', gotoTagTiddler);
    // 如何在这里监听change 事件, 从而实现自动刷新主题
  },

  // TODO: need refresh manually here
  shouldUpdate(_, changedTiddlers) {
    // tw里面即使是一个state tiddler变换都会触发这个函数的调用
    // changeTiddlers 会包含一些系统tiddler的状态变换tiddler, 应该去掉
    // log 输出不了, 但是alert却可以
    const filteredChangedTiddlers = Object.keys(changedTiddlers).filter(
      (tiddler) =>
        tiddler === '$:/info/darkmode' ||
        (!tiddler.startsWith('$:/') && !tiddler.startsWith('Draft of')),
    );
    // TODO
    return filteredChangedTiddlers.length ? true : false;
    // return true;
  },
};

// default export
module.exports = TagPie;
","module-type":"echarts-component","type":"application/javascript","description":"tag pie on tiddlywiki"},"$:/plugins/oeyoews/neotw/modules/darkmode/action":{"title":"$:/plugins/oeyoews/neotw/modules/darkmode/action","<!-- tags":"$:/tags/StartupAction/PostRender -->","text":"<!-- dark mode -->
